              "defaultValue": "3"
            },
            {
              "id": "region",
              "type": "TXT",
              "label": "Clockify region override",
              "description": "Optional region code (euc1/use2/euw2/apse2). Leave empty to auto-detect.",
              "required": false,
              "defaultValue": ""
            }
          ]
        }
      ]
    }
  ]
}
